# Transactions!!!


account_1 = "0x902f8fc364E7a4572f0626eecB54Ff600aea180e"
account_2 = "0x35892aDEA3965ED9B5f093B46823779fAC0294Ee"
private_key = "705c3bfa922e65ca13db8638797e07cf90f66c51c08ff7772c7202c8fbea7c6a"

# Get the nonce // prevents you of sending a tx twice
nonce = web3.eth.getTransactionCount(account_1)

# Build a transaction
tx = {
    'nonce': nonce,
    'to': account_2,
    'value': web3.toWei(1, 'ether'),
    'gas': 2000000,
    'gasPrice': web3.toWei('50', 'gwei')
}

# Sign transaction
signed_tx = web3.eth.account.signTransaction(tx, private_key)
# Send transaction
tx_hash = web3.eth.sendRawTransaction(signed_tx.rawTransaction)
# Get transaction hash
print(web3.toHex(tx_hash))